import { ADD_FAV, REMOVE_FAV } from "./actions";

const initialState = {
    myFavorites: [],
};

const rootReducer = (state = initialState, {type, payload}) => {
    switch (type) {
        case ADD_FAV:
            return {...state, myFavorites: [...state.myFavorites, payload]}
            break;
        case REMOVE_FAV:
            return {...state, myFavorites: state.myFavorites.filter(element => element.id !== parseInt(payload))}
            break;
        default:
            return {...state}
            break;
    }
}

export default rootReducer;
