const validation = (data) => {
    const emailRegex = /^[-\w.%+]{1,64}@(?:[A-Z0-9-]{1,63}\.){1,125}[A-Z]{2,63}$/i;
    const passRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[$@$!%*?&])[A-Za-z\d$@$!%*?&]{6,9}/;
    
    const { email, password } = data;

    const estado = {mail:'', pass: ''}

    if(!email) estado.mail = 'Campo Email Vacio';
        else if(email.length > 35) estado.mail = 'No ingrese mas de 35 caracteres';
        else if(!emailRegex.test(email)) estado.mail = 'Email invalido';
    if(!password) estado.pass = 'Campo vacio';
        else if(!passRegex.test(password)) estado.pass = 'La clave debe tener al menos un numero';
        else if(password.length > 5 && password.length < 9) estado.pass = 'La clave debe tener entra 6 y 8 caracteres';

    return estado;
};

export default validation;
