const validation = (data) => {
    const emailRegex = /^[-\w.%+]{1,64}@(?:[A-Z0-9-]{1,63}\.){1,125}[A-Z]{2,63}$/i;
    const passRegex = /[0-9]/;
    
    const { email, password } = data;

    const estado = {mail:'', pass: ''}

    if(!email) estado.mail = 'Campo Email Vacio';
    if(email.length > 35) estado.mail = 'No ingrese mas de 35 caracteres';
    if(!emailRegex.test(email)) estado.mail = 'Email invalido';
        if(!password) estado.pass = 'Campo vacio';
        if(password.length >= 6 || password.length <= 10) estado.pass = 'La clave debe tener entra 6 y 8 caracteres';
        if(!passRegex.test(password)) estado.pass = 'La clave debe tener al menos un numero';

    return estado;
};

export default validation;
