const validation = (data, funcError, dataError) => {
    const emailRegex = /^[-\w.%+]{1,64}@(?:[A-Z0-9-]{1,63}\.){1,125}[A-Z]{2,63}$/i;
    if(!data.email) funcError({...dataError, email:'Campo vacio'});
    else{
        if(data.email.length < 36){
            if(emailRegex.test(data.email)) funcError({...dataError, email:''});
            else{
                funcError({...dataError, email:'Email invalido'});
            }
        }
        else{funcError({...dataError, email:'No ingrese mas de 35 caracteres'});}
    }
};

export default validation;
