import style from './App.module.css';
import Cards from './components/Cards/Cards.jsx';
import Nav from './components/Nav/Nav.jsx';
import { useState } from 'react';
import axios from 'axios';

function App() {
   const [characters, setCharacters] = useState([]);

   
   function onSearch(id) {
      const filtrarIds = (element) => element.id != id;
      if(characters.some(filtrarIds())){
      axios(`https://rickandmortyapi.com/api/character/${id}`)
      .then(({ data }) => {
         if (data.name) {
            setCharacters((oldChars) => [...oldChars, data]);
         } else {
            window.alert('¡No hay personajes con este ID!');
         }
      })}
      else {window.alert('REPETIDO');}
   };

   const onClose = (id) => {
      const charactersFiltrado = characters.filter((caracter) => caracter.id != parseInt(id));
      setCharacters(charactersFiltrado);
   };

   return (
      <div className={style.App}>
         <Nav onSearch={onSearch}/>
         <Cards characters={characters} onClose={onClose}/>
      </div>
   );
}

export default App;
