import style from './App.module.css';
import Cards from './components/Cards/Cards.jsx';
import Nav from './Views/Nav/Nav.jsx';
import { useState } from 'react';
import axios from 'axios';
import {Routes, Route} from 'react-router-dom';

function App() {
   const [characters, setCharacters] = useState([]);

   function onSearch(id) {
      if(id > 0 && id < 827){
      axios(`https://rickandmortyapi.com/api/character/${id}`)
      .then(({ data }) => {
            if(!characters.some((character) => character.id === data.id)){
               setCharacters((oldChars) => [...oldChars, data]);
            }
            else {
               window.alert('Ya fue agregado ese ID!');
            }
            })}
      else{
         window.alert('¡No hay personajes con este ID!');
      }
   };
   
   const onClose = (id) => {
      const charactersFiltrado = characters.filter((caracter) => caracter.id !== parseInt(id));
      setCharacters(charactersFiltrado);
   };

   return (
      <div className={style.App}>
         <Nav onSearch={onSearch}/>
         <Routes>
            <Route path='/home'>
               <Home characters={characters} onClose={onClose}/>
            </Route>
            <Route path='/about'/>
            <Route path='/detail/:id'/>
         </Routes>
      </div>
   );
}

export default App;
