const validation = (data, funcError, dataError) => {
    const { email, password } = data;

    const isEmailValid = (email) => {
      const emailRegex = /^[-\w.%+]{1,64}@(?:[A-Z0-9-]{1,63}\.){1,125}[A-Z]{2,63}$/i;
      return emailRegex.test(email);
    };
  
    if (!email) {
      setError({ ...errorMessages, email: 'Campo vacío' });
    } else if (email.length > 35) {
      setError({ ...errorMessages, email: 'No ingrese más de 35 caracteres' });
    } else if (!isEmailValid(email)) {
      setError({ ...errorMessages, email: 'Email inválido' });
    } else {
      setError({ ...errorMessages, email: '' });
    }
  
    if (!password) {
      setError({ ...errorMessages, password: 'Campo vacío' });
    } else if (password.length < 6 || password.length > 10) {
      setError({ ...errorMessages, password: 'La clave debe tener entre 6 y 10 caracteres' });
    } else if (!password.includes('0-9')) {
      setError({ ...errorMessages, password: 'La clave debe tener al menos un número' });
    } else {
      setError({ ...errorMessages, password: '' });
    }
};

export default validation;
