const validation = (data, funcError, dataError) => {
    const emailRegex = /^[-\w.%+]{1,64}@(?:[A-Z0-9-]{1,63}\.){1,125}[A-Z]{2,63}$/i;
    const passRegex = /[0-9]/;
    if(!data.email) funcError({...dataError, email:'Campo vacio'});
    else if(!data.password) funcError({...dataError, password:'Campo vacio'});
    else if(data.password.length >= 6 && data.password.length <= 10){
        if(passRegex.test(data.password)) funcError({...dataError, password:''});
            else{
                funcError({...dataError, password:'La clave debe tener al menos un numero'});
            }
    }
    else if(data.email.length < 36){
            if(emailRegex.test(data.email)) funcError({...dataError, email:''});
            else{
                funcError({...dataError, email:'Email invalido'});
            }
    }
    else if (data.email.length > 36){funcError({...dataError, email:'No ingrese mas de 35 caracteres'});}
    else{funcError({...dataError, password:'La clave debe tener entra 6 y 8 caracteres'})}

};

export default validation;
