const validation = (data, funcError, dataError) => {
    console.log('data' , data, 'funcError', funcError, 'dataError', dataError)
    const emailRegex = /^[-\w.%+]{1,64}@(?:[A-Z0-9-]{1,63}\.){1,125}[A-Z]{2,63}$/i;
    const passRegex = /[0-9]/;
    const { email, password } = data;
    
    if(!email) funcError({...dataError, email:'Campo Email Vacio'});
    if(email.length > 35) funcError({...dataError, email:'No ingrese mas de 35 caracteres'});
    if(emailRegex.test(email)) funcError({...dataError, email:''});
            else{
                funcError({...dataError, email:'Email invalido'});
            }
    if(!password) funcError({...dataError, password:'Campo  ps vacio'});
    if(password.length >= 6 || password.length <= 10) funcError({...dataError, password:'La clave debe tener entra 6 y 8 caracteres'});
    if(passRegex.test(password)) funcError({...dataError, password:''});
            else{
                funcError({...dataError, password:'La clave debe tener al menos un numero'});
            }
};

export default validation;
